SELECT a.amount AS amount, e.sssNo AS sssNo, CONCAT_WS(', ', setNameToUpper(e.lastname), CONCAT_WS(' ', setNameToUpper(e.firstname), setNameToUpper(e.middlename))) AS name, b.name AS branch, tn.name AS tradeName, cn.name AS corporateName, tn.sssNo AS erSssNo, b.address AS address, s.payrollDate AS payrollDate FROM employee e INNER JOIN payroll_table s ON e.employeeId = s.employeeId INNER JOIN advance_table a ON s.id = a.payrollId INNER JOIN branch_table b ON e.branchId = b.id  INNER JOIN trade_table tn ON b.tradeId = tn.id INNER JOIN corporate_table cn ON tn.corporateId = cn.id WHERE (SELECT branchId FROM employee_contribution_main WHERE employeeId = e.employeeId ORDER BY id DESC LIMIT 1) = $P{BRANCH_ID} AND s.payrollDate = $P{PAYROLL_DATE} AND a.advanceType = 'sss loan' AND a.rowStatus IS NULL AND 
(s.actionTaken = 'adjusted' OR s.actionTaken IS NULL) ORDER BY e.lastname ASC